@app.route('/api/video-info', methods=['POST'])
def get_video_info():
    """API Ù„Ø¬Ù„Ø¨ Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„ÙÙŠØ¯ÙŠÙˆ"""
    try:
        data = request.get_json()
        url = data.get('url', '').strip()
        
        if not url:
            return jsonify({"error": "Ø§Ù„Ø±Ø¬Ø§Ø¡ Ø¥Ø¯Ø®Ø§Ù„ Ø±Ø§Ø¨Ø· Ø§Ù„ÙÙŠØ¯ÙŠÙˆ"}), 400

        # Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª yt-dlp
        ydl_opts = {
            "quiet": True,
            "skip_download": True,
            "nocheckcertificate": True,
            "extractor_args": {
                "youtube": {
                    "player_client": ["android", "web"]
                }
            }
        }

        # Ø¥Ø¶Ø§ÙØ© Ù…Ù„Ù Ø§Ù„ÙƒÙˆÙƒÙŠØ² Ø¥Ø°Ø§ ÙƒØ§Ù† Ù…ÙˆØ¬ÙˆØ¯Ø§Ù‹
        if os.path.exists(COOKIES_FILE):
            ydl_opts["cookiefile"] = COOKIES_FILE

        # Ø¬Ù„Ø¨ Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„ÙÙŠØ¯ÙŠÙˆ
        with yt_dlp.YoutubeDL(ydl_opts) as ydl:
            info = ydl.extract_info(url, download=False)

            downloads = []
            for f in info.get("formats", []):
                if f.get("url") and f.get("vcodec") != "none":
                    downloads.append({
                        "format_id": f.get("format_id"),
                        "ext": f.get("ext"),
                        "resolution": f.get("resolution") or f.get("format_note") or "Unknown",
                        "filesize_mb": round(f.get("filesize", 0) / (1024*1024), 2) if f.get("filesize") else None,
                        "download_url": f.get("url")
                    })

            response_data = {
                "title": info.get("title"),
                "uploader": info.get("uploader"),
                "duration_seconds": info.get("duration"),
                "views": info.get("view_count"),
                "thumbnail": info.get("thumbnail"),
                "downloads": downloads
            }

            return jsonify(response_data)

    except Exception as e:
        return jsonify({"error": str(e)}), 500

